import React, { Component } from 'react'
import API from '../utils/API';
import Saved from '../components/Saved';

class SavedBooks extends Component {
    state = {
        books: []
    };

    componentDidMount() {
        API.getSavedBooks()
            .then((res) => {
                this.setState({
                    books: res.data.map((e, i) => ({
                        _id: e._id,
                        bookId: e.bookId,
                        title: e.title,
                        authors: e.authors.join(', '),
                        description: e.description,
                        image: e.image,
                        link: e.info
                    }))
                })
            })
    };

    handleDeleteBook(id) {
        console.log(id);
        // const book = this.state.books.items.find((book) => book.id === id);
        API.deleteBook(id)
            .then(() => this.componentDidMount)
            .catch(err => console.log(err));
    };

    render() {
        console.log(this.state.books);
        let savedList;
        if(this.state.books.length) {
            savedList = this.state.books.map(
                (book) => (
                        <Saved 
                            key={book._id}
                            title={book.title}
                            authors={book.authors}
                            description={book.description}
                            image={book.image}
                            link={book.link}
                            Button={() => (
                                <button 
                                    onClick={() => this.handleDeleteBook(book.bookId)}>
                                    Delete
                                </button>
                            )}
                        /> 
                )
            )
        } else {
            savedList = <h5>Search for a book you enjoy and click save to begin adding books to this page</h5>
        };
                    
        return (
            <div>
                <h5>Saved Books</h5>
                {savedList}
            </div>
        )
    }  
}

export default SavedBooks;
