import React, { useState } from 'react';
import GoogleBar from '../components/GoogleBar/GoogleBar';
import Results from '../components/Results/Results';
import API from '../utils/API';


//send searchTerm, input function and api function to GoogleBar component
//send map of book results to Results component
const Search = () => {
    const [books, setBooks] = useState([]);

    const handleSearch = (event) => {
        setBooks({searchTerm: event.target.value.toLowerCase() });
    }


    const handleBookSearch = () => {
        API.searchBooks(books.searchTerm)
            .then((res) => {
                console.log(res);
                // setBooks(
                //     res.data.map((e, i) => ({
                //         key: i,
                //     }))
                // )
            })
    }



    
    return (
        <div>
            <GoogleBar handleSearch={handleSearch} handleBookSearch={handleBookSearch}/>
            <Results />
        </div>
    )  
}

export default Search;
